import styles from './padrao.module.css';
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';
import alterar from '../assets/alterar.png';
import Excluir from '../components/excluir';
import React from "react";
import Menu from '../components/menu';
import { useLocation } from 'react-router-dom';

export default function Estoque() {
  const [post, setPost] = useState(null);
  const location = useLocation();

  const fetchData = async () => {
    axios.get('http://127.0.0.1:5000/api/list_estoque')
      .then(response => {
        setPost(response.data);
      })
      .catch(error => {
        console.log(error);
      });
    } 
  useEffect(() => {
    fetchData();
  }, []);

  if(!post) 
    return ("carregando...") ; 


  return (
    <>
    {location.pathname === '/pages/estoque' && <Menu />} 

    <div className={styles.containerEstoque}>
    <div className={styles.estoque}>
        <h1 className={styles.titulo}>Estoque</h1>
        <table>
            <thead className={styles.linhasTabela}>
                <tr>
                    <th>ID</th>
                    <th>Produto</th>
                    <th>Quantidade</th>
                    <th>Serial</th>
                    <th>Descrição</th>
                </tr>
            </thead>
            <tbody>
                {post.map(post => (
                    <tr key={post.id}>
                        <td>{post.id}</td>
                        <td>{post.produto}</td>
                        <td>{post.quantidade}</td>
                        <td>{post.serial}</td>
                        <td>{post.descricao}</td>
                        <td><Link to={{ pathname:`/pages/alterar/${post.id}`}} className={styles.link_menu} ><img className={styles.imgEstoque} src={alterar}/></Link></td>
                        <td><Excluir id={post.id} /></td>

                    </tr>

                ))}
            </tbody>
        </table>
    </div>

    </div>

    </>
);

}
