import styles from './padrao.module.css';
import Estoque from './estoque';
import Grafico from '../components/sidebar';
import React from "react";
import Menu from '../components/menu';
import { Outlet, useLocation } from 'react-router-dom';
import Cadastrar from './cadastrar';
import Home from './home';



export default function Index() {
    const location = useLocation();
    let content;

    switch (location.pathname) {
        case '/':
            content = <Home />;
            break;
        case '/cadastrar':
            content = <Cadastrar />;
            break;
        case '/estoque':
            content = <Estoque />; // Assuming you have an Estoque component
            break;
        case '/grafico':
            content = <Grafico />; // Assuming you have a Grafico component
            break;
        // Add more cases for other routes as needed
        default:
            content = <Home />; // Default to Home if no match
            break;
    }

    return (
        <>

            <Menu />

            {content}
        </>
    );
}
