import React, { useState } from "react";
import './App.css';
import Login from './pages/login.js';
import Home from './pages/home.js';
import Estoque from './pages/estoque'; // Importe Estoque
import Config from './pages/config'; // Importe Config
import Cadastrar from './pages/cadastrar'; // Importe Cadastrar
import Alterar from './pages/alterar'; // Importe Alterar
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import PrivateRoute from './pages/rota_login'; // Importe PrivateRoute

function App() {
  const [isAuthenticated, setIsAuthenticated ] = useState(localStorage.getItem('token') !== null);
  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
  };

  return (
    <>
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Login onLoginSuccess={handleLoginSuccess} />} />
        <PrivateRoute path="/pages/home" element={<Home />} isAuthenticated={isAuthenticated} />
        <PrivateRoute path="/pages/estoque" element={<Estoque />} isAuthenticated={isAuthenticated} />
        <PrivateRoute path="/pages/config" element={<Config />} isAuthenticated={isAuthenticated} />
        <PrivateRoute path="/pages/cadastrar" element={<Cadastrar />} isAuthenticated={isAuthenticated} />
        <PrivateRoute path="/pages/alterar/:id" element={<Alterar />} isAuthenticated={isAuthenticated} />

    </Routes>
    </BrowserRouter>
    </>
  );
}

export default App;
